mapper.select:
WITH BORD AS ( SELECT * FROM COM_CODE WHERE COM_CODE_GRP = 'SEC008' ) SELECT A.* FROM ( SELECT ROWNUM RNUM ,B.BO_NO, B.BO_WRTER, B.BO_DATE, B.BO_TITLE, B.BO_CN, B.BO_CNT, BR.COM_CODE_NM BD_SE, B.ATCH_FILE_NO, S.SKLSTF_NO, S.SKLSTF_NM FROM BOARD B JOIN STAFF S ON B.BO_WRTER = S.SKLSTF_NO INNER JOIN BORD BR ON (BR.COM_CODE = B.BD_SE)
WHERE BR.COM_CODE = '01' ORDER BY RNUM DESC ) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

WITH BORD AS ( SELECT * FROM COM_CODE WHERE COM_CODE_GRP = 'SEC008' ) SELECT A.* FROM ( SELECT ROWNUM RNUM ,B.BO_NO, B.BO_WRTER, B.BO_DATE, B.BO_TITLE, B.BO_CN, B.BO_CNT, BR.COM_CODE_NM BD_SE, B.ATCH_FILE_NO, S.SKLSTF_NO, S.SKLSTF_NM FROM BOARD B JOIN STAFF S ON B.BO_WRTER = S.SKLSTF_NO INNER JOIN BORD BR ON (BR.COM_CODE = B.BD_SE)
WHERE BR.COM_CODE = '02' ORDER BY RNUM DESC ) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
