mapper.insert:
/* kr.ac.usu.classroom.mapper.StudentClassRoomMapper.insertSubmitTestAnswer */ INSERT ALL
INTO SUBMIT_ANSWER_PAPER (STDNT_NO,TEST_QUES_NO,TEST_SE,LCTRE_NO,SUBMIT_ASWPER) VALUES (#{item.stdntNo,jdbcType=VARCHAR}, #{item.testQuesNo,jdbcType=VARCHAR}, #{item.testSe,jdbcType=VARCHAR}, #{item.lctreNo,jdbcType=VARCHAR}, #{item.submitAswper,jdbcType=VARCHAR})
SELECT * FROM DUAL

mapper.select:
/* kr.ac.usu.classroom.mapper.StudentClassRoomMapper.selectTestResultInfo */
SELECT 
	NVL(SUM(CASE WHEN
		(SELECT TEST_ASWPER FROM TEST_ANSWER_PAPER TAP WHERE TEST_QUES_NO = SAP.TEST_QUES_NO AND LCTRE_NO = SAP.LCTRE_NO AND TEST_SE = SAP.TEST_SE)
		=
		SAP.SUBMIT_ASWPER
			THEN (
					SELECT 100/(SELECT COUNT(*) FROM TEST_ANSWER_PAPER TAP WHERE LCTRE_NO = #{lctreNo} AND TEST_SE = #{testSe})
					FROM DUAL
				) ELSE 0 END
	),0)
	FROM SUBMIT_ANSWER_PAPER SAP
	WHERE LCTRE_NO = #{lctreNo} AND TEST_SE = #{testSe} AND STDNT_NO = #{stdntNo}

mapper.insert:
/* kr.ac.usu.classroom.mapper.StudentClassRoomMapper.selectTestResultInfo */

	SELECT
		TO_CHAR(SYSDATE,'YYMMDD')||LPAD(NVL(MAX(SUBSTR(ATCH_FILE_NO,7)),0)+1,8,'0')
	FROM
		ATTACHING_FILE
	WHERE
		SUBSTR(ATCH_FILE_NO,1,6) = TO_CHAR(SYSDATE,'YYMMDD')

INSERT INTO ATTACHING_FILE (
	ATCH_FILE_NO
	, ATCH_FILE_SN
	, ATCH_FILE_NM
	, ATCH_FILE_STRE_NM
	, ATCH_FILE_STRE_PATH
	, ATCH_FILE_MG
	, ATCH_FILE_INDICT_MG
	, ATCH_FILE_MIME_TYPE
) VALUES (
	#{atchFileNo,jdbcType=VARCHAR}
	, (SELECT NVL(MAX(ATCH_FILE_SN),0)+1 FROM ATTACHING_FILE WHERE ATCH_FILE_NO = #{atchFileNo,jdbcType=VARCHAR})
	, #{atchFileNm,jdbcType=VARCHAR}
	, #{atchFileStreNm,jdbcType=VARCHAR}
	, #{atchFileStrePath,jdbcType=VARCHAR}
	, #{atchFileMg,jdbcType=NUMERIC}
	, #{atchFileIndictMg,jdbcType=VARCHAR}
	, #{atchFileMimeType,jdbcType=VARCHAR}
)

