mapper.select:
SELECT C.CR_NO , C.CR_SE , C.CR_TITLE , C.CR_CN , C.CR_WRTER , C.CR_DATE , C.LCTRE_NO , (SELECT CC.COM_CODE_NM FROM COM_CODE CC WHERE CC.com_code_grp = 'SEC015' AND CC.COM_CODE = L.LCTRE_SE) LCTRE_SE , L.SEMSTR_SE , (SELECT AA.COM_CODE_NM FROM COM_CODE AA WHERE AA.com_code_grp = 'SEC014' AND AA.COM_CODE = L.COMPL_SE) COMPL_SE , L.PRFSOR_NO , R.STDNT_NO , R.ATCH_FILE_NO , M.STDNT_NM , M.STDNT_TELNO , G.SKNRG_STTUS_GRADE , G.SKNRG_STTUS_MAJOR1 , F.ATCH_FILE_NM , F.ATCH_FILE_INDICT_MG , F.ATCH_FILE_MIME_TYPE
FROM CLASSROOM_BOARD C INNER JOIN LECTURE L ON (C.LCTRE_NO = L.LCTRE_NO) INNER JOIN LECTURE_SUBMIT R ON (R.CR_NO = C.CR_NO) INNER JOIN STUDENT_INFORMATION M ON (M.STDNT_NO = R.STDNT_NO) INNER JOIN SCHOOL_REGISTER_STATUS G ON (G.STDNT_NO = R.STDNT_NO) INNER JOIN ATTACHING_FILE F ON (F.ATCH_FILE_NO = R.ATCH_FILE_NO)
WHERE C.CR_NO = #{crNo,jdbcType=VARCHAR}
