mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, B.* FROM( SELECT A.CNSLT_NO , C.STDNT_NM , A.STDNT_NO , D.CNSLT_SE ,(SELECT CC.COM_CODE_NM FROM COM_CODE CC WHERE CC.com_code_grp = 'SEC007' AND CC.COM_CODE = D.CNSLT_SE) CNSLT_SE_NM , TO_CHAR(TO_DATE(A.CNSLT_DATE)) CNSLT_DATE , D.CNSLT_REQUST_CN , E.COM_CODE_NM TMTB_SE , A.CNSLT_DTLS , D.CNSLT_REQUST_DATE FROM CONSULTATION A INNER JOIN STUDENT_INFORMATION C ON (A.STDNT_NO = C.STDNT_NO) INNER JOIN CONSULTATION_REQUEST D ON (A.CNSLT_NO = D.CNSLT_NO) LEFT OUTER JOIN COM_CODE E ON (A.TMTB_SE = E.COM_CODE)
WHERE E.COM_CODE_GRP = 'SEC011' AND D.CNSLT_SE='01' ) B
) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT A.* FROM ( SELECT ROWNUM RNUM, B.* FROM( SELECT A.CNSLT_NO , A.STDNT_NO , A.CNSLT_REQUST_DATE , A.CNSLT_SE , A.CNSLT_REQUST_TIME , A.CNSLT_REQUST_CN , A.CNSLT_REQUST_RETURN , B.STDNT_NM , E.SUBJCT_NM SKNRG_STTUS_MAJOR1 , C.COM_CODE_NM CNSLT_REQUST_PROCESS FROM CONSULTATION_REQUEST A INNER JOIN STUDENT_INFORMATION B ON(A.STDNT_NO = B.STDNT_NO) INNER JOIN COM_CODE C ON (A.CNSLT_REQUST_PROCESS = C.COM_CODE) INNER JOIN SCHOOL_REGISTER_STATUS D ON (A.STDNT_NO = D.STDNT_NO) INNER JOIN SUBJECT E ON (D.SKNRG_STTUS_MAJOR1 = E.SUBJCT_NO) WHERE C.COM_CODE_GRP = 'SEC013' AND A.CNSLT_SE = '01' ) B
) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE CONSULTATION_REQUEST
	SET
	    CNSLT_REQUST_PROCESS = '03'
	    ,CNSLT_REQUST_RETURN = #{cnsltRequstReturn,jdbcType=VARCHAR}
WHERE
    CNSLT_NO = #{cnsltNo,jdbcType=VARCHAR}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, B.* FROM( SELECT A.CNSLT_NO , A.STDNT_NO , A.CNSLT_REQUST_DATE , A.CNSLT_SE , (SELECT Q.COM_CODE_NM FROM com_code Q WHERE Q.com_code_grp = 'SEC011' AND Q.COM_CODE = A.CNSLT_REQUST_TIME) CNSLT_REQUST_TIME , A.CNSLT_REQUST_CN , A.CNSLT_REQUST_RETURN , B.STDNT_NM , E.SUBJCT_NM SKNRG_STTUS_MAJOR1 , C.COM_CODE_NM CNSLT_REQUST_PROCESS
FROM CONSULTATION_REQUEST A INNER JOIN STUDENT_INFORMATION B ON(A.STDNT_NO = B.STDNT_NO) INNER JOIN COM_CODE C ON (A.CNSLT_REQUST_PROCESS = C.COM_CODE) INNER JOIN SCHOOL_REGISTER_STATUS D ON (A.STDNT_NO = D.STDNT_NO) INNER JOIN SUBJECT E ON (D.SKNRG_STTUS_MAJOR1 = E.SUBJCT_NO)
WHERE C.COM_CODE_GRP = 'SEC013' AND A.CNSLT_SE = '01' AND A.CNSLT_REQUST_PROCESS = '02' AND A.CNSLT_REQUST_RETURN IS NULL AND A.CNSLT_NO NOT IN (SELECT CNSLT_NO FROM CONSULTATION)
) B
) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:
INSERT INTO CONSULTATION (
    CNSLT_NO,
    CNSLT_CNSLTNT,
    STDNT_NO,
    CNSLT_DATE,
    TMTB_SE,
    CNSLT_DTLS
) VALUES (
    #{cnsltNo,jdbcType=VARCHAR}
    ,#{cnsltCnsltnt,jdbcType=VARCHAR}
    ,#{stdntNo,jdbcType=VARCHAR}
    ,TO_CHAR(TO_DATE(#{cnsltDate,jdbcType=DATE}, 'YYYY-MM-DD'), 'YY/MM/DD')
    ,#{tmtbSe,jdbcType=VARCHAR}
    ,#{cnsltDtls,jdbcType=CLOB}
)
