mapper.update:
UPDATE professor
SET
    prfsor_zip = #{prfsorZip}
    , prfsor_adres1 = #{prfsorAdres1}
    , prfsor_adres2 = #{prfsorAdres2}
    , prfsor_telno = #{prfsorTelno}
WHERE
    prfsor_no = #{prfsorNo}

UPDATE professor
SET
    prfsor_password = #{prfsorPassword}
WHERE
    prfsor_no = #{prfsorNo}
