mapper.update:
UPDATE staff
SET
    sklstf_zip = #{sklstfZip}
    , sklstf_adres1 = #{sklstfAdres1}
    , sklstf_adres2 = #{sklstfAdres2}
    , sklstf_telno = #{sklstfTelno}
WHERE
    sklstf_no = #{sklstfNo}

UPDATE staff
SET
    sklstf_password = #{sklstfPassword}
WHERE
    sklstf_no = #{sklstfNo}
